import { Component } from '@angular/core';
import { PeopleServise } from './people.servise';

@Component({
  selector: 'app-task2',
  templateUrl: './task2.component.html',
  styleUrls: ['./task2.component.css'],
  providers: [PeopleServise]
})
export class Task2Component {
  item:string;
  items:string[]=[];
  selectedItem:string|undefined;
  index:number=-1;
  isAdd:boolean=true;


  SaveClick(item:string):void{
    this.items[this.index] = item;
    this.peopleService.SetData(this.items);
    this.selectedItem=undefined;
    this.isAdd=true;
  }

  OnSelect(item:string,i:number):void{
    this.selectedItem = item;
    this.index = i;
    this.isAdd=false;
  }

  AddClick(){
    this.peopleService.AddData(this.item);
  }

  constructor(private peopleService: PeopleServise){

  }
  ngOnInit(){
    this.items=this.peopleService.GetData();
  }
}
<h1>Task 2</h1>

<div *ngIf="selectedItem">
    <input type="text" [(ngModel)]="items[index]">
    <button (click)="SaveClick(items[index])">Save</button>
</div>

<div [hidden]="!isAdd">
    <input type="text" [ngModel]="item">
    <button (click)="AddClick()">Add</button>
</div>

<div>
    <h1>Famous people:</h1>
    <div [class.active]="item===selectedItem" class="div" *ngFor="let item of items;let i=index" (click)="OnSelect(item,i)">
        {{item}}
    </div>
</div>
